import { RouterLink } from "vue-router"
</script>

<script>
export default {
	data() {
		return {
			isScrolled: false,
			isMenuActive: false,
			isPortfolioHovered: false,
			isDropdownActive: false,
			isDropdownVisible: false, // Set to true for bigger screens, false for mobile screens
			isDropdownCards: false,
			isActive: false,
			activeItem: null,
			isOHCHovered: false,
			allGone: false,
			isDropdownServicesVisible: false, // New property for the second dropdown

		}
	},
	mounted() {
		const storedHoverState = localStorage.getItem("ohcHoverState")
		if (storedHoverState !== null) {
			this.isOHCHovered = JSON.parse(storedHoverState)
			if (!this.isOHCHovered) {
				this.hideDropdown() // Hide dropdown if not hovered
			}
		}
	},

	computed: {
		isSluzbyActive() {
			return true
		},
	},
	setup() {
		const updateDropdownActive = () => {
			const currentPath = Router.currentRoute.value.path
			this.isDropdownActive = currentPath.startsWith("/sluzby")
		}

		onBeforeMount(() => {
			updateDropdownActive()
			Router.beforeEach((next) => {
				updateDropdownActive()
				next()
			})
		})
	},
	watch: {
    // Watch for route changes and hide the dropdown
    '$route'() {
      this.hideDropdown();
    },
  },
  beforeRouteUpdate(to, from, next) {
    // Hide the dropdown when a route is updated
    this.hideDropdown();
    next();
  },


	mounted() {
		window.addEventListener("scroll", this.handleScroll)
	},
	beforeDestroy() {
		window.removeEventListener("scroll", this.handleScroll)
	},

	methods: {
		showDropdown() {
			this.isDropdownVisible = true
		},
		hideDropdown() {
			this.isDropdownVisible = false
		},
		showcards() {
			this.isDropdownCards = true
		},
		hidecards() {
			this.isDropdownCards = false
		},
		
		hideAll() {
			this.isDropdownVisible = false
			this.isDropdownCards = false
		},

		toggleDropdown() {
			this.isDropdownVisible = !this.isDropdownVisible
			this.hideCards()
		},
		toggleDropdownS() {
			this.isDropdownCards = !this.isDropdownCards
		},

		handleOHCHover() {
			this.isOHCHovered = true
		},
		handleOHCLeave() {
			this.isOHCHovered = false
			this.hideDropdown() // Hide dropdown when leaving content
		},
		showCards() {
			this.isDropdownCards = true
		},
		hideCards() {
			this.isDropdownCards = false
		},

		handleScroll() {
			if (window.pageYOffset > 20) {
				this.isScrolled = true
			} else {
				this.isScrolled = false
			}
		},

		closeClick() {
			if (window.innerWidth <= 1400) {
				mainListDiv.style.display = "none"
				this.isMenuActive = !this.isMenuActive
			}
		},

		toggleMenu() {
			if (window.innerWidth <= 1400) {
				this.isMenuActive = !this.isMenuActive
				const mainListDiv = document.getElementById("mainListDiv")
				if (this.isMenuActive) {
					mainListDiv.style.display = "block"
				} else {
					mainListDiv.style.display = "none"
					this.isDropdownActive = false
					this.isDropdownVisible = false // Hide dropdown when menu is closed
				}
			}
		},
	},
}
</script>

